package IfAndIfelseConditions;

public class IfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 30;
		int y = 25;
		
		if(x>y)
		{
			System.out.println("x is greater than y");
		}
		
		else
		{
			System.out.println("x is less than y");
		}
	}

}
